import Foundation
import XCTest
@testable import Chip8

class ScreenTests: XCTestCase {
    
    func testScreenDrawing() {
        let screen = Screen()
        
        let x = Emulator.Hardware.screenColumns - 1
        let y = Emulator.Hardware.screenRows - 1
        screen.drawPixel(x: x, y: y)
        
        XCTAssert(screen.isSet(x: x, y: y), "Pixel should be on")
        
        let pixel = screen.pixelAt(x: x, y: y)
        XCTAssert(pixel.color == .white)
        
        screen.clear()
        XCTAssert(!screen.isSet(x: x, y: y), "Pixel should be off")
        XCTAssert(pixel.color == .black)
    }
    
